            TOUS_HEADING: 'TERMS OF USE/SERVICE ("TOUS")',
            TOUS_DATE: 'Effective Date: April 27, 2015',
            TOUS_HEADER1: '1. INTRODUCTION TO SERVICE AND ACCEPTANCE',
            TOUS_HEADER2: '2. CHANGES TO THE TOUS',
            TOUS_HEADER3: '3. REGISTRATION AND SUBSCRIPTION',
